import React,{useState} from 'react';

const Main = () => {
    const [tasks,setTasks] = useState([
        {
            id:1,
            task:"Pay Bills",
            check:true
        },
        {
            id:2,
            task:"Go Shopping",
            check:true
        },
        {
            id:3,
            task:"See the Doctor",
            check:false
        }
    ]);


    return (
        <>
            <div class="container">
                <h2>TODO LIST</h2>
                <h3>Add Item</h3>
                <p>
                    <input id="new-task" type="text" /><button className="btn">Add</button>
                </p>

                <h3>Todo</h3>
                <ul id="incomplete-tasks" onclick="eventList(event)">
                    {tasks.map((val)=>{
                        return val.check ? <><li><input type="checkbox" className="checkbox" checked={val.check} /><label>{val.task}</label><input type="text" /><button
                        className="edit">Edit</button><button className="delete">Delete</button></li></>:<></>
                    })}
                </ul>

                <h3>Completed</h3>
                <ul id="completed-tasks">
                {tasks.map((val)=>{
                        return val.check ? <></>:<><li><input type="checkbox" className="checkbox" /><label>Pay Bills</label><input type="text" /><button
                        className="edit">Edit</button><button className="delete">Delete</button></li></>
                    })}
                </ul>
            </div>
        </>
    )
}

export default Main;
