import React,{useState} from 'react';

const Main = ({tasks}) => {

    return (
        <>
            <div className="container">
                <h2>TODO LIST</h2>
                <h3>Add Item</h3>
                <p>
                    <input id="new-task" type="text" /><button className="btn">Add</button>
                </p>

                <h3>Todo</h3>
                <ul id="incomplete-tasks" >
                    {tasks.map((val)=>{
                        return val.check ?<></>: <><li key={val.id}><input type="checkbox" className="checkbox" checked={val.check} /><label>{val.task}</label><input type="text" /><button
                        className="edit">Edit</button><button className="delete">Delete</button></li></>
                    })}
                </ul>

                <h3>Completed</h3>
                <ul id="completed-tasks">
                {tasks.map((val)=>{
                        return val.check ?<><li><input type="checkbox" className="checkbox" checked={val.check}  /><label>{val.task}</label><input type="text" /><button
                        className="edit">Edit</button><button className="delete">Delete</button></li></>:<></>
                    })}
                </ul>
            </div>
        </>
    )
}

export default Main;
