import React,{useState} from 'react';
import Main from './main';

const App = () => {
  const [tasks,setTasks] = useState([
    {
        id:1,
        task:"Pay Bills",
        check:true
    },
    {
        id:2,
        task:"Go Shopping",
        check:true
    },
    {
        id:3,
        task:"See the Doctor",
        check:false
    }
]);

const deleteTask = (id) =>{
  setTasks(tasks.filter((val)=>val.id!==id))
  console.log("delete",{id});
}
  return (
   <><Main tasks={tasks} onDelete={deleteTask}/></>
  )
}

export default App
